'use client'

import { useState } from 'react';
import Navbar from 'app/components/Navbar';
import Footer from 'app/components/Footer';
import { Toaster, toast } from 'react-hot-toast';
import { FiShoppingCart } from 'react-icons/fi';
import { MagnifyingGlassIcon } from '@heroicons/react/24/solid';

const films = [
  {
    title: 'Warkop DKI Reborn: Jangkrik Boss! Part 1',
    img: 'data:image/jpeg;base64,',
    description: 'Petualangan kocak Dono, Kasino, dan Indro dalam versi modern dari legenda komedi Indonesia.'
  },
  {
    title: 'Get Married',
    img: 'https://upload.wikimedia.org/wikipedia/id/9/9c/Get_Married.jpg',
    description: 'Kisah cinta dan persahabatan di kampung yang penuh dengan kelucuan khas anak muda Betawi.'
  },
  {
    title: 'My Stupid Boss',
    img: 'https://down-id.img.susercontent.com/file/bb040506367f71a75d83837e1b85aea1',
    description: 'Seorang pegawai kantor harus menghadapi bosnya yang sangat absurd dan menguji kesabaran.'
  },
  {
    title: 'Marmut Merah Jambu',
    img: 'https://images.tokopedia.net/img/cache/1500/hDjmkQ/2022/6/21/776349f8-ac8c-4377-81eb-88240407b4ad.jpg',
    description: 'Kisah masa SMA yang konyol namun manis tentang cinta pertama dan persahabatan.'
  },
  { title: 'Dumb and Dumber', img: 'https://media.karousell.com/media/photos/products/2024/8/30/vcd_film_dumb__dumber_1724999824_9379c67e_progressive', description: 'Komedi klasik tentang dua sahabat bodoh yang terlibat dalam petualangan kocak.' },
  { title: 'The Hangover', img: 'https://musicart.xboxlive.com/7/83ec0500-0000-0000-0000-000000000002/504/image.jpg?q=90&m=6&h=270&w=270&b=%23FFFFFFFF&f=jpg&o=f&aim=true', description: 'Sekelompok pria terbangun setelah pesta bachelor tanpa ingatan dan mencari tahu apa yang terjadi.' },
  { title: '3 Idiots', img: 'https://down-id.img.susercontent.com/file/id-11134201-7r98r-lp9jauxuvrzu2c', description: 'Film India yang mengisahkan tiga mahasiswa teknik dengan cara unik dalam menghadapi kehidupan dan pendidikan.' },
  { title: 'Superbad', img: 'https://down-id.img.susercontent.com/file/id-11134207-7r98y-llgeb7qvvuaa86', description: 'Dua remaja berusaha untuk menghadiri pesta besar sebelum lulus SMA, menghadapi berbagai situasi lucu.' },
  { title: 'The Intouchables', img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-xz0tiVFApgXHDbts9N_lVnqzw1YzMvnKmA&s', description: 'Kisah persahabatan antara seorang pria kaya yang lumpuh dan pengasuhnya yang berasal dari latar belakang berbeda.' },
  { title: 'The Grand Budapest Hotel', img: 'https://i.ebayimg.com/images/g/LzIAAOSwj~NlcLCQ/s-l1200.jpg', description: 'Komedi gelap karya Wes Anderson yang berlatar di hotel mewah Eropa Timur dengan berbagai karakter eksentrik.' },
  { title: 'The Secret Zoo', img: 'https://i.ebayimg.com/images/g/IKoAAOSwUWpg13ru/s-l400.jpg', description: 'Seorang pegawai kantor yang menyamar sebagai hewan di kebun binatang untuk menyelamatkan tempat tersebut.' },
  { title: 'Me Time', img: 'https://awsimages.detik.net.id/community/media/visual/2022/08/29/me-time-2022.jpeg?w=650&q=80', description: 'Seorang ayah yang sibuk akhirnya memiliki waktu sendiri dan terlibat dalam petualangan tak terduga.' },
];

export default function Shop() {
  const [searchQuery, setSearchQuery] = useState('');
  const [cartItems, setCartItems] = useState<any[]>([]);
  const [cartOpen, setCartOpen] = useState(false);

  const filteredFilms = films.filter(film =>
    film.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const addToCart = (film: any) => {
    setCartItems([...cartItems, film]);
    toast.success(`${film.title} added to cart!`);
  };

  const clearCart = () => {
    setCartItems([]);
    toast('Cart emptied.');
  };

  return (
    <div>
      <Navbar />
      <Toaster />

      {/* Floating Cart Icon with Badge */}
      <div className="fixed top-20 mt-3 right-4 z-50 ">
        <div className="relative">
          <button
            className="bg-blue-600 text-white p-3 rounded-full shadow-lg hover:bg-blue-700"
            onClick={() => setCartOpen(!cartOpen)}
          >
            <FiShoppingCart size={24} />
            {cartItems.length > 0 && (
              <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full px-2 py-0.5">
                {cartItems.length}
              </span>
            )}
          </button>

          {cartOpen && (
            <div className="absolute right-0 mt-2 w-64 bg-white shadow-lg rounded-lg p-4">
              <h2 className="text-lg font-semibold mb-2">Your Cart</h2>
              {cartItems.length === 0 ? (
                <p className="text-gray-500">Cart is empty.</p>
              ) : (
                <ul className="space-y-2">
                  {cartItems.map((item, index) => (
                    <li key={index} className="text-sm border-b pb-1">{item.title}</li>
                  ))}
                </ul>
              )}
              <button
                className="mt-4 bg-red-500 text-white w-full py-1 rounded hover:bg-red-600"
                onClick={clearCart}
              >
                Empty Cart
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Product Listing */}
      <div className="container mx-auto p-10">
      <section className="text-center mb-10 px-4">
      <h1 className="text-4xl font-bold text-gray-800">Our Products</h1>
      <p className="text-lg text-gray-500 mt-2">Temukan berbagai produk berkualitas di toko kami!</p>

      <div className="mt-6 flex justify-center">
        <div className="relative w-full max-w-md">
          <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
          <input
            type="text"
            placeholder="Cari film favoritmu..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-10 pr-4 py-2 text-sm rounded-full border border-gray-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
          />
        </div>
      </div>
    </section>

        <section className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
          {filteredFilms.map((film, index) => (
            <div key={index} className="bg-white shadow-md rounded-lg p-4">
              <img src={film.img} alt={film.title} className="w-full h-48 object-cover mb-4 rounded" />
              <h2 className="text-xl font-semibold mb-2">{film.title}</h2>
              <p className="text-gray-700 mb-2">{film.description}</p>
              <button
                className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
                onClick={() => addToCart(film)}
              >
                Add to Cart
              </button>
            </div>
          ))}
        </section>
      
      </div>
      <Footer />
    </div>
  );
}
